package my_package;

public class Swaping_of_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10, b=20;
		System.out.println("The current value of a is: "  +a);
		System.out.println("The current value of b is: "  +b);
		System.out.println("\n");
		int c;
		c=a;
		a=b;
		b=c;
		System.out.println("The swapped value of a as: "  +a);
		System.out.println("The swapped value of b as: "  +b);
	}

}
